import{_ as e,c as o,o as r,a as s}from"./app.a067e1d4.js";const u=JSON.parse('{"title":"Cors","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u76F8\u5173\u914D\u7F6E","slug":"\u76F8\u5173\u914D\u7F6E","link":"#\u76F8\u5173\u914D\u7F6E","children":[]}],"relativePath":"others/cors.md","lastUpdated":1669599731000}'),t={name:"others/cors.md"},c=s('<h1 id="cors" tabindex="-1">Cors <a class="header-anchor" href="#cors" aria-hidden="true">#</a></h1><p>\u6D4F\u89C8\u5668\u51FA\u4E8E\u5B89\u5168\u8003\u8651\uFF0C\u4F1A\u9650\u5236\u4E0D\u540C\u57DF\u7684\u8D44\u6E90\u8BF7\u6C42\uFF0C\u4F46\u662F\u5728\u5B9E\u9645\u573A\u666F\u4E2D\uFF0C\u5F88\u591A\u60C5\u51B5\u4E0B\u90FD\u9700\u8981\u8DE8\u57DF\u3002</p><p><a href="https://developer.mozilla.org/zh-CN/docs/Web/HTTP/CORS" target="_blank" rel="noreferrer">\u8DE8\u8D44\u6E90\u5171\u4EAB CORS</a></p><h2 id="\u76F8\u5173\u914D\u7F6E" tabindex="-1">\u76F8\u5173\u914D\u7F6E <a class="header-anchor" href="#\u76F8\u5173\u914D\u7F6E" aria-hidden="true">#</a></h2><p>\u5728\u8BF7\u6C42\u8DE8\u57DF\u65F6\uFF0C\u4F1A\u6709\u4E00\u6B21\u5BA2\u6237\u7AEF\u548C\u670D\u52A1\u7AEF\u7684\u534F\u5546\u5904\u7406\uFF0C\u7B2C\u4E00\u6B21\u8DE8\u57DF\u8BF7\u6C42\u8FD4\u56DE\u7684\u72B6\u6001\u7801\u662F 204 \u3002</p><p>\u5728\u534F\u5546\u8FC7\u7A0B\u4E2D\uFF0C\u53EF\u4EE5\u5BF9\u4E00\u4E9B\u5934 ( header ) \u5C5E\u6027\u8FDB\u884C\u6821\u9A8C\u3002</p><ul><li>Origin\uFF1A\u9996\u90E8\u5B57\u6BB5\u8868\u660E\u9884\u68C0\u8BF7\u6C42\u6216\u5B9E\u9645\u8BF7\u6C42\u7684\u6E90\u7AD9\u3002</li><li>Access-Control-Request-Method\uFF1A\u9996\u90E8\u5B57\u6BB5\u7528\u4E8E\u9884\u68C0\u8BF7\u6C42\uFF0C\u5176\u4F5C\u7528\u662F\u5C06\u5B9E\u9645\u8BF7\u6C42\u6240\u4F7F\u7528\u7684 HTTP \u65B9\u6CD5\u544A\u8BC9\u670D\u52A1\u5668\u3002</li><li>Access-Control-Request-Headers\uFF1A\u9996\u90E8\u5B57\u6BB5\u7528\u4E8E\u9884\u68C0\u8BF7\u6C42\uFF0C\u5176\u4F5C\u7528\u662F\u5C06\u5B9E\u9645\u8BF7\u6C42\u6240\u643A\u5E26\u7684\u9996\u90E8\u5B57\u6BB5\u544A\u8BC9\u670D\u52A1\u5668\u3002</li></ul><p>\u8FD8\u6709\u4E00\u4E9B\u54CD\u5E94 header \u7684\u8BBE\u7F6E\u5982\u4E0B\uFF1A</p><ul><li>Access-Control-Allow-Origin\uFF1A\u5BF9\u4E8E\u4E0D\u9700\u8981\u643A\u5E26\u8EAB\u4EFD\u51ED\u8BC1\u7684\u8BF7\u6C42\uFF0C\u670D\u52A1\u5668\u53EF\u4EE5\u6307\u5B9A\u54EA\u4E9B\u57DF\u53EF\u4EE5\u8BF7\u6C42\u3002\u4F8B\u5982\uFF0C<code>Access-Control-Allow-Origin:https://koajs.com/</code> \u8868\u793A\u53EA\u5141\u8BB8\u6765\u81EA <code>https://koajs.com/</code> \u7684\u8BF7\u6C42\u3002\u5982\u679C\u670D\u52A1\u7AEF\u6307\u5B9A\u4E86\u5177\u4F53\u7684\u57DF\u540D\uFF0C\u800C\u975E <code>*</code> \uFF0C\u90A3\u4E48\u54CD\u5E94\u9996\u90E8\u4E2D\u7684 Vary \u5B57\u6BB5\u7684\u503C\u5FC5\u987B\u5305\u542B Origin \uFF0C\u8FD9\u5C06\u544A\u8BC9\u5BA2\u6237\u7AEF\uFF0C\u670D\u52A1\u5668\u5BF9\u4E0D\u540C\u7684\u6E90\u7AD9\u8FD4\u56DE\u4E0D\u540C\u7684\u5185\u5BB9\u3002</li><li>Access-Control-Expose-Headers\uFF1A\u5728\u8DE8\u6E90\u8BBF\u95EE\u65F6\uFF0CXMLHttpRequest \u5BF9\u8C61\u7684 <code>getResponseHeader()</code> \u65B9\u6CD5\u53EA\u80FD\u5F97\u5230\u4E00\u4E9B\u6700\u57FA\u672C\u7684\u54CD\u5E94\u5934\uFF0C\u5982 <code>Cache-Control</code> \u3001<code>Content-Language</code> \u3001<code>Content-Type</code> \u3001<code>Expires</code> \u3001<code>Last-Modified</code> \u3001<code>Pragma</code> \u3002\u5982\u679C\u8981\u8BBF\u95EE\u5176\u4ED6\u5934\uFF0C\u5219\u9700\u8981\u670D\u52A1\u5668\u8BBE\u7F6E\u672C\u54CD\u5E94\u5934\uFF0C\u5982 <code>Access-Control-Expose-Headers:X-My-Custom-Header,X-Another-Custom-Header</code> \uFF0C\u8FD9\u6837\u6D4F\u89C8\u5668\u5C31\u80FD\u591F\u901A\u8FC7 <code>getResponseHeader()</code> \u8BBF\u95EE <code>X-My-Custom-Header</code> \u548C <code>X-Another-Custom-Header</code> \u54CD\u5E94\u5934\u4E86\u3002</li><li>Access-Control-Max-Age\uFF1A\u6307\u5B9A\u4E86 preflight \u8BF7\u6C42\u7684\u7ED3\u679C\u80FD\u591F\u88AB\u7F13\u5B58\u591A\u4E45\u3002</li><li>Access-Control-Allow-Credentials\uFF1A\u6307\u5B9A\u4E86\u5F53\u6D4F\u89C8\u5668\u7684 credentials \u8BBE\u7F6E\u4E3A true \u65F6\uFF0C\u662F\u5426\u5141\u8BB8\u6D4F\u89C8\u5668\u8BFB\u53D6 response \u7684\u5185\u5BB9\u3002\u8FD9\u4E2A\u53C2\u6570\u8868\u793A\u5728\u9884\u8BF7\u6C42 ( preflight ) \u4E2D\uFF0C\u662F\u5426\u53EF\u4EE5\u4F7F\u7528 credentials \u5B57\u6BB5\u3002\u8BF7\u6CE8\u610F\uFF0C\u7B80\u5355 GET \u8BF7\u6C42\u4E0D\u4F1A\u88AB\u9884\u68C0\uFF0C\u5982\u679C\u5BF9\u6B64\u7C7B\u8BF7\u6C42\u7684\u54CD\u5E94\u4E2D\u4E0D\u5305\u542B\u8BE5\u5B57\u6BB5\uFF0C\u8FD9\u4E2A\u54CD\u5E94\u5C06\u88AB\u5FFD\u7565\uFF0C\u5E76\u4E14\u6D4F\u89C8\u5668\u4E5F\u4E0D\u4F1A\u5C06\u76F8\u5E94\u5185\u5BB9\u8FD4\u56DE\u7ED9\u7F51\u9875\u3002</li><li>Access-Control-Allow-Methods\uFF1A\u9996\u90E8\u5B57\u6BB5\u7528\u4E8E\u9884\u68C0\u8BF7\u6C42\u7684\u54CD\u5E94\uFF0C\u6307\u660E\u4E86\u5B9E\u9645\u8BF7\u6C42\u6240\u5141\u8BB8\u4F7F\u7528\u7684 HTTP \u65B9\u6CD5\u3002</li><li>Access-Control-Allow-Headers\uFF1A\u9996\u90E8\u5B57\u6BB5\u7528\u4E8E\u9884\u68C0\u8BF7\u6C42\u7684\u54CD\u5E94\uFF0C\u6307\u660E\u4E86\u5B9E\u9645\u8BF7\u6C42\u4E2D\u5141\u8BB8\u643A\u5E26\u7684\u9996\u90E8\u5B57\u6BB5\u3002</li></ul>',9),d=[c];function a(l,i,n,p,h,_){return r(),o("div",null,d)}const A=e(t,[["render",a]]);export{u as __pageData,A as default};
